import React, { Component } from "react";

export class newitem extends Component {

  timeminute = (oldTime)=>{
    var seconds = Math.floor((new Date() - date) / 1000);

  var interval = seconds / 31536000;

  if (interval > 1) {
    return Math.floor(interval) + " years";
  }
  interval = seconds / 2592000;
  if (interval > 1) {
    return Math.floor(interval) + " months";
  }
  interval = seconds / 86400;
  if (interval > 1) {
    return Math.floor(interval) + " days";
  }
  interval = seconds / 3600;
  if (interval > 1) {
    return Math.floor(interval) + " hours";
  }
  interval = seconds / 60;
  if (interval > 1) {
    return Math.floor(interval) + " minutes";
  }
  return Math.floor(seconds) + " seconds";


  }
  render() {
    let { title, description, urlImage, url, idName, author, time } = this.props;
    return (
      <div>
        <div
          className="card p-1 border-success my-2"
          style={{ width: "17rem" }}
        >
          <img style={{ height: "10rem" }}
            src={
              urlImage
                ? urlImage
                : "https://i.ibb.co/DWyYBH8/Markus-winkler-a-B4-BJSZo-TTI-unsplash.jpg"
            }
            className="card-img-top"
            alt="..."
          />
          <div className="card-body">
            <h5 className="card-title">{title ? title : ""}</h5>
            <p className="card-text">{description ? description : ""}...</p>
            <a className="btn btn-sm btn-outline-success" href={url} target="_blank" rel="noreferrer">
              Refer to {idName}
            </a>
            <hr />
            <p class="card-text"><small class="text-muted">By {author} | {onload= this.timeminute(new Date(time))}</small></p>
          </div>
        </div>
      </div>
    );
  }
}

export default newitem;
