import React, { Component } from "react";
import Newitem from "./newitem";
import Masonry from "react-masonry-css";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import ScrollToTop from "./scrollToTop";import InfiniteScroll from "react-infinite-scroll-component";


export class news extends Component {
  static defaultProps = {
    country: "in",
    pageSize: 12,
    category: "technology",
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor() {
    super();

    this.state = {
      news: [],
      loading: false,
      page: 1,
      totalResults: 0
    };
  }

  async updateNews() {
    
  }

  // async componentDidMount() {
  //   this.updateNews();
  // }

  // handleNextClick = async () => {
  //   this.setState({
  //     page: this.state.page + 1,
  //   });
  //   this.updateNews();
  // };

  // handlePrevClick = async () => {
  //   this.setState({
  //     page: this.state.page - 1,
  //   });
  //   this.updateNews();
  // };
  fetchMoreData = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&sortBy=publishedAt&apiKey=f9aa6d33d92045f7b656982d9957fe8b
    &page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      news: this.state.news.cancat(parsedData.articles),
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }

  render() {
    const breakpointColumnsObj = {
      default: 4,
      1100: 3,
      700: 2,
      500: 1,
    };

    return (
      <div>
        <div className="container my-3">
          <h1>
            {/* <strong style={{ color: "green" }}>DiemFlash </strong> */}
            <u>Top-Headlines</u>
          </h1>
          <InfiniteScroll
          dataLength={this.state.news.length}
          next={this.fetchMoreData}
          hasMore={this.state.news.length !== this.state.totalResults}
          loader={<Spinner/>}
        >
          {/* {this.state.loading && <Spinner />} */}
          <Masonry
            breakpointCols={breakpointColumnsObj}
            className="my-masonry-grid row"
            columnClassName="my-masonry-grid_column"
          >
            {this.state.news.map((element) => {
              return (
                <Newitem
                  title={
                    element.title
                      ? element.title.length > 80
                        ? element.title.slice(0, 80) + "..."
                        : element.title
                      : ""
                  }
                  description={
                    element.description
                      ? element.source.name === "PRNewswire"
                        ? element.description.slice(
                            element.description.indexOf("--") + 2,
                            138
                          )
                        : element.description.slice(0, 90)
                      : ""
                  }
                  url={element.url}
                  urlImage={element.urlToImage}
                  idName={element.source.name}
                  time={
                    element.publishedAt.slice(0, 10) +
                    " " +
                    element.publishedAt.slice(11, 18)
                  }
                  author={element.author ? element.author : "Unknown"}
                />
              );
            })}
          </Masonry>
          </InfiniteScroll>
        </div>
        <div className="container d-flex justify-content-between my-4 position-static fixed-bottom">
          {/* <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-success"
            onClick={this.handlePrevClick}
          >
            &larr; Prev
          </button> */}
          <ScrollToTop />
          {/* <button
            disabled={
              this.state.page + 1 >
              Math.ceil(this.state.totalResults / this.props.pageSize)
            }
            type="button"
            className="btn btn-success"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button> */}
        </div>
      </div>
    );
  }
}

export default news;
