import React, { Component } from "react";
import Newitem from "./newitem";
import Masonry from "react-masonry-css"

export class news extends Component {
  news = {
    status: "ok",
    totalResults: 60582,
    articles: []
  }
  
  constructor(props) {
    super(props);

    this.state = {
      news: this.news,
      loading: false,
    };
  }

  async componentDidMount() {
    let url =
      "https://newsapi.org/v2/everything?q=health&from=2021-08-09&sortBy=publishedAt&apiKey=f9aa6d33d92045f7b656982d9957fe8b";
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({ news: parsedData });
  }

  render() {
    const breakpointColumnsObj = {
      default: 4,
      1100: 3,
      700: 2,
      500: 1
    };
    return (
      <div>
        <div className="container my-3">
          <h1>
            <strong style={{ color: "green" }}>DiemFlash </strong>
            <u>Top-Headlines</u>
          </h1>
          <Masonry
            breakpointCols={breakpointColumnsObj}
            className="my-masonry-grid row"
            columnClassName="my-masonry-grid_column"
          >
            {this.state.news.articles.map((element) => {
              return (
                  <Newitem
                    title={element.title.slice(0, 45)}
                    description={
                      element.description
                        ? element.description.slice(0, 90)
                        : ""
                    }
                    url={element.url}
                    urlImage={element.urlToImage}
                    idName={element.source.name}
                  />
              );
            })}
          </Masonry>
        </div>
      </div>
    );
  }
}

export default news;
