import React, { Component } from "react";

class ScrollToTop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      visible: false
    };
  }
  componentDidMount() {
    var scrollComponent = this;
    document.addEventListener("scroll", function(e) {
      scrollComponent.toggleVisibility();
    });
  }

  toggleVisibility() {
    if (window.pageYOffset > 300) {
      this.setState({
        visible: true
      });
    } else {
      this.setState({
        visible: false
      });
    }
  }

  scrollToTop() {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }
  render() {
    return (
      <div className="scroll-to-top">
        {(
          <div onClick={() => this.scrollToTop()}>
            <img src='https://img.icons8.com/ios-filled/50/000000/up-arrow.png' alt='Go to top'/>
          </div>
        )}
      </div>
    );
  }
}
export default ScrollToTop
