import React, { Component } from "react";

export class newitem extends Component {
  timeminute = (date) => {
    date.setHours(date.getHours() + 5);
    date.setMinutes(date.getMinutes() + 30);
    var seconds = Math.floor((new Date() - date) / 1000);

    var interval = seconds / 31536000;

    if (interval > 1) {
      return Math.floor(interval) + " years";
    }
    interval = seconds / 2592000;
    if (interval > 1) {
      return Math.floor(interval) + " months";
    }
    interval = seconds / 86400;
    if (interval > 1) {
      return (
        Math.floor(interval) + (Math.floor(interval) > 1 ? " Day" : " Days")
      );
    }
    interval = seconds / 3600;
    if (interval > 1) {
      return (
        Math.floor(interval) + (Math.floor(interval) > 1 ? " Hour" : " Hours")
      );
    }
    interval = seconds / 60;
    if (interval > 1) {
      return (
        Math.floor(interval) +
        (Math.floor(interval) > 1 ? " Minute" : " Minutes")
      );
    }
    return "Just Now";
  };
  render() {
    let { title, description, urlImage, url, idName, author, time } =
      this.props;
    return (
      <div>
        <div
          className="card p-1 border-success my-2"
          style={{ width: "17rem" }}
        >
          
          
          <img
            style={{ height: "10rem" }}
            src={
              urlImage
                ? urlImage
                : "https://i.ibb.co/DWyYBH8/Markus-winkler-a-B4-BJSZo-TTI-unsplash.jpg"
            }
            className="card-img-top"
            alt="..."
          />
          <div className="card-body">
          <span
            className="position-absolute top-0 translate-middle badge round-pill bg-danger"
            style={{ left: "90%", zIndex: "1" }}
          >
            {idName}
          </span>
            <h5 className="card-title">{title ? title : ""}</h5>
            
            <p className="card-text">{description ? description : ""}...</p>
            <a
              className="btn btn-sm btn-outline-success"
              href={url}
              target="_blank"
              rel="noreferrer"
            >
              Refer to {idName}
            </a>
            <hr />
            <p classname="card-text">
              <small className="text-muted">
                By {author} | {this.timeminute(new Date(time))} ago
              </small>
            </p>
          </div>
        </div>
      </div>
    );
  }
}

export default newitem;
